INSERT INTO `activity` (`id`, `title`, `remark`, `mediaId`, `mediaType`, `content`, `length`, `fromCourseId`, `fromCourseSetId`, `fromUserId`, `startTime`, `endTime`, `createdTime`, `updatedTime`, `copyId`, `migrateLessonId`) VALUES (1,'如何创建课程？',NULL,1,'text','<p><span style=\"font-size:14px;\">1、教师身份</span></p>\n\n<p><span style=\"font-size:14px;\">路径：网站首页【我的教学】-【创建课程】。</span></p>\n\n<p><span style=\"font-size:14px;\">2、管理员身份</span></p>\n\n<p><span style=\"font-size:14px;\">路径：【管理后台】-【课程】-【课程管理】-【创建课程】，只有管理员有权限进入管理后台。</span></p>\n\n<p><span style=\"font-size:14px;\">详细教程：</span><span style=\"font-size:16px;\"><strong><a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></span></p>\n',0,1,1,1,0,0,1516008405,0,0,0),(2,'如何设置课程教师和课程价格？',NULL,2,'text','<p><strong><span style=\"font-size:16px;\">1、设置课程教师</span></strong></p>\n\n<p>路径：【课程管理页】-【教师设置】。</p>\n\n<p>说明：只能添加有教师权限的用户为课程老师；</p>\n\n<p><span style=\"font-size:14px;\"><strong>如何给某个用户教师权限？</strong></span></p>\n\n<p>路径：【管理后台】-【用户】-【用户管理】，搜索到要提升权限的用户，点击操作一栏下的【设置用户组】，勾选对应权限保存即可。</p>\n\n<p> </p>\n\n<p><strong><span style=\"font-size:16px;\">2、课程价格</span></strong></p>\n\n<p>路径：【课程管理页】-【营销设置】-【加入设置】-【价格·收费】设置数值即为人民币价格；</p>\n\n<p><strong>会员价格</strong>：可设置某一会员等级免费学习该门课程，前提，安装会员插件并在【管理后台】-【系统】-【用户设置】中设置会员等级。</p>\n\n<p> </p>\n',0,1,1,1,0,0,1516008430,0,0,0),(3,'如何上传视频让学员学习？',NULL,3,'text','<p><span style=\"font-size:16px;\">路径：【课程管理页】-【计划任务】-【添加任务】-【选择教学手段·视频】。</span></p>\n\n<p><span style=\"font-size:16px;\"><strong>上传视频——发布任务——发布课程——学员加入课程——观看视频。</strong></span></p>\n\n<p> </p>\n\n<p> </p>\n',0,1,1,1,0,0,1516008459,0,0,0),(4,'如何添加作业练习和考试？',NULL,4,'text','<p>前提：课程的【题目管理】中已添加题目。</p>\n\n<p><strong>作业练习：</strong></p>\n\n<p>在【计划任务】中添加任务，选择教学手段：作业或练习，按提示设置保存即可。</p>\n\n<p><strong>考试：</strong></p>\n\n<p>在课程的【试卷管理】中，创建试卷，发布试卷，然后到【计划任务】中添加任务，选择教学手段：试卷，按提示设置保存即可。</p>\n\n<p>确保课程已发布，学员加入该课程，即可看到作业练习和试卷。</p>\n',0,1,1,1,0,0,1516008485,0,0,0),(5,'如何添加学习资料让学员下载？',NULL,5,'text','<p>路径：【课程管理页】-【计划任务】-【添加任务】-【选择教学手段·下载资料】，上传资料并保存。</p>\n\n<p> </p>\n\n<p>确保课程已发布，学员加入课程后，可在课程【资料区】中下载资料。</p>\n',0,1,1,1,0,0,1516008522,0,0,0),(6,'管理员如何管理课程和设置教师权限？',NULL,6,'text','<p><strong>1、管理课程</strong></p>\n\n<p>查看和管理网站所有课程，路径：【管理后台】-【课程】-【课程管理】。</p>\n\n<p><strong>2、设置教师权限</strong></p>\n\n<p>如果需要限制教师的权限，比如不允许教师设置课程价格，不允许教师添加或移除课程学员，设置路径：</p>\n\n<p>【管理后台】-【系统】-【课程管理】-【课程】-【权限与版权】。</p>\n\n<p>说明：ES目前有管理员的权限分配系统，但教师的详细权限分配暂未实现。</p>\n\n<p> </p>\n\n<p> </p>\n',0,1,1,1,0,0,1516008543,0,0,0),(7,'如何查看课程订单和营收？',NULL,7,'text','<p><strong>1、查看课程订单</strong></p>\n\n<p>（1）学员，【账户中心】-【我的订单】可以查看自己的课程订单。</p>\n\n<p>（2）教师/管理员/超管，【课程管理页】-【订单查询】可以查看购买该课程的学员订单；前提：超级管理员开启了允许教师查看课程订单，【管理后台】-【系统】-【课程设置】-【课程】中开启。</p>\n\n<p>（3）管理员/超管，【管理后台】-【交易】-【用户订单】中查看所有用户购买课程的订单。</p>\n\n<p><strong>2、查看营收</strong></p>\n\n<p>（1）查看网站整体营收，路径【管理后台】-【交易】-【网校账单】，查看网校净收入/收入/支出；还可根据起始时间/支付方式/类型等查看网站收入。</p>\n\n<p>（2）查看单个课程的营收，【课程管理页】-【订单查询】-【导出】订单统计课程收入。</p>\n\n<p> </p>\n',0,1,1,1,0,0,1516008568,0,0,0),(8,'关闭课程与删除课程',NULL,8,'text','<p>关闭课程与删除课程路径都在网站后台，需管理员以上权限才能操作，教师无权操作。</p>\n\n<p><strong>关闭课程：</strong></p>\n\n<p>路径：【管理后台】-【课程】-【课程管理】-【关闭课程】；</p>\n\n<p>只有已发布的课程，才有关闭的概念，关闭后还可以再次发布。</p>\n\n<p>关闭后，未加入课程的学员访问将提示课程未发布无法加入；已经加入课程的学员，可以继续学习课程内容，如果不希望学员继续学习，可在课程的学员管理中移除学员。</p>\n\n<p><strong>删除课程：</strong></p>\n\n<p>路径：【管理后台】-【课程】-【课程管理】-【删除课程】；</p>\n\n<p>删除后与课程相关的学习记录都将无法恢复，学员无法访问课程，<span style=\"color:#e74c3c;\"><strong>删除操作不可逆</strong></span>，请慎重操作，删除时需要输入登陆密码再次确认。</p>\n\n<p> </p>\n',0,1,1,1,0,0,1516008595,0,0,0);
